                <li>
                    <a href="#" id="logout-button">
                        <span class="icon">
                                <ion-icon name="log-out-outline"></ion-icon>
                            </span>
                        <span class="title">Cerra sesi√≥n</span>
                    </a>
                </li>
            </ul>
        </div>


        <div class="main">
            <div class="topbar">
                <div class="toggle">
                    <ion-icon name="menu-outline"></ion-icon>
                </div>

                <div class="search">
                    <label>
                            <input type="text" placeholder="Search here">
                            <ion-icon name="search-outline"></ion-icon>
                        </label>
                </div>

                <div class="user">
                    <img src="data:image/png;base64," alt>
                </div>
            </div>

            <section>



                <style>
                    .container-pqrs {
                        max-width: 75%;
                        margin: 30px auto;
                        padding: 20px;
                        background-color: #fff;
                        box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
                    }
                    
                    h1 {
                        text-align: center;
                        font-size: 40px;
                        margin-bottom: 30px;
                        text-transform: uppercase;
                        letter-spacing: 5px;
                    }
                    
                    strong {
                        font-size: 19px;
                        text-transform: uppercase;
                    }
                    
                    .pqrs-item {
                        background-color: #f0f0f0;
                        padding: 10px;
                        margin-bottom: 10px;
                        display: flex;
                        flex-wrap: wrap;
                        justify-content: space-between;
                        align-items: center;
                    }
                    
                    .pqrs-info {
                        flex-grow: 1;
                        padding: 5px;
                    }
                    
                    .pqrs-buttons {
                        display: flex;
                        justify-content: flex-end;
                    }
                    
                    button {
                        width: 100px;
                        height: 40px;
                        background-color: #01c75a;
                        color: #fff;
                        border: none;
                        border-radius: 5px;
                        padding: 5px 10px;
                        cursor: pointer;
                        margin-left: 5px;
                        transition: background-color 0.3s;
                    }
                    
                    button.respond {
                        background-color: #f31000;
                    }
                    
                    button:hover {
                        background-color: #2980b9;
                    }
                </style>
                </head>

                <body>
                    <div class="container-pqrs">
                        <h1>Lista de PQRS</h1>


                        <div class="pqrs-item" data-id="1">
                            <div class="pqrs-info">
                                <span><strong>Tipo:</strong> Queja</span> <br><br>
                                <span><strong>Descripci√≥n:</strong> Producto defectuoso</span>
                            </div>
                            <div class="pqrs-buttons">
                                <button class="delete">Eliminar</button>
                                <button class="respond">Responder</button>
                            </div>
                        </div>

                        <div class="pqrs-item" data-id="2">
                            <div class="pqrs-info">
                                <span><strong>Tipo:</strong> Sugerencia</span> <br><br>
                                <span><strong>Descripci√≥n:</strong> Agregar m√°s variedad de productos</span>
                            </div>
                            <div class="pqrs-buttons">
                                <button class="delete">Eliminar</button>
                                <button class="respond">Responder</button>
                            </div>
                        </div>

                        <div class="pqrs-item" data-id="3">
                            <div class="pqrs-info">
                                <span><strong>Tipo:</strong> Petici√≥n</span> <br><br>
                                <span><strong>Descripci√≥n:</strong> Cambiar horario de atenci√≥n</span>
                            </div>
                            <div class="pqrs-buttons">
                                <button class="delete">Eliminar</button>
                                <button class="respond">Responder</button>
                            </div>
                        </div>

                        <div class="pqrs-item" data-id="4">
                            <div class="pqrs-info">
                                <span><strong>Tipo:</strong> Reclamo</span> <br><br>
                                <span><strong>Descripci√≥n:</strong> Factura incorrecta</span>
                            </div>
                            <div class="pqrs-buttons">
                                <button class="delete">Eliminar</button>
                                <button class="respond">Responder</button>
                            </div>
                        </div>

                        <div class="pqrs-item" data-id="5">
                            <div class="pqrs-info">
                                <span><strong>Tipo:</strong> Queja</span> <br><br>
                                <span><strong>Descripci√≥n:</strong> Entrega tard√≠a</span>
                            </div>
                            <div class="pqrs-buttons">
                                <button class="delete">Eliminar</button>
                                <button class="respond">Responder</button>
                            </div>
                        </div>
                    </div>

                    <script>
                        document.addEventListener("DOMContentLoaded", function() {
                            const pqrsList = document.querySelectorAll(".pqrs-item");

                            pqrsList.forEach(function(item) {
                                const deleteButton = item.querySelector(".delete");
                                const respondButton = item.querySelector(".respond");

                                deleteButton.addEventListener("click", function() {
                                    const confirmed = confirm("¬øEst√°s seguro de eliminar esta PQRS?");
                                    if (confirmed) {
                                        item.remove();
                                    }
                                });

                                respondButton.addEventListener("click", function() {
                                    // Aqu√≠ puedes implementar la l√≥gica para responder a la PQRS
                                    const pqrsId = item.getAttribute("data-id");
                                    alert("Responder a la PQRS con ID: " + pqrsId);
                                });
                            });
                        });
                    </script>


            </section>

            <script src="/form/dash.js"></script>


            <script type="module" src="https://unpkg.com/ionicons@5.5.2/dist/ionicons/ionicons.esm.js"></script>
            <script nomodule src="https://unpkg.com/ionicons@5.5.2/dist/ionicons/ionicons.js"></script>
            </body>

</html>